import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

// Em ambos os casos, o código demonstrado serve para recuperar os dados através da URL
// exemplo: localhost:3000/airlines/ALGUM-VALOR
// console.log(params) => ALGUM-VALOR

// react router dom 5
// const Airline = (props) => {
// console.log(props.params.match.slug)
// const uri = props.params.match.slug
// const url = `/airlines/${uri}`

// react router dom 6
// import { useParams } from 'react-router-dom'
// const params = useParams()
// console.log(params)

const Airline = (props) => {

    const params = useParams()

    const [airline, setAirline] = useState([])

    useEffect(() => {
        axios.get(`/api/v1/${params}`)
            .then(response => {
                setAirline(response)
            })
            .catch(response => {
                console.log(response)
            })
    }, [])

    return (


        <div className="card">
            <div className="airline-logo">
               <img src={props.attribute.image_url} alt={props.attributes.name}/>
            </div>
            <div className="airline-name">{props.attributes.name}</div>
            <div className="airline-score">{props.attributes.avg_score}</div>
            <div className="airline-link">
                <a href={'/airlines/${props.attributes.slug}'}>View Airline</a>
            </div>
        </div>
    )
}

export default Airline
